interface MenuItem {
  id: string;
  label: string;
  icon: React.ComponentType<{ active?: boolean; color?: "default" | "active" | "primary" }>;
  href: string;
  submenu?: { label: string; href: string }[];
}

const menuItems: MenuItem[] = [
  { id: "bazaar-ai", label: "Bazaar AI", icon: AIIcon, href: "/ai-chat" },
  { id: "bazar-podcasts", label: "Bazaar Podcasts", icon: SettingsIcon, href: "/bazar-podcasts" },
  { 
    id: "trading", 
    label: "Trading", 
    icon: DashboardIcon, 
    href: "/trading",
    submenu: [
      { label: "Platform", href: "/trading/platform" },
      { label: "Trading", href: "/trading/trading" },
      { label: "Bots", href: "/trading/bots" },
      { label: "Copy Trading", href: "/trading/copy" },
    ]
  },
  { id: "bazaar", label: "Bazaar", icon: PortfolioIcon, href: "/bazaar" },
  { id: "competitions", label: "Competitions", icon: UsersIcon, href: "/competitions" },
  { 
    id: "announcements", 
    label: "Bazaar Announcements", 
    icon: NotificationIcon, 
    href: "/announcements",
    submenu: [
      { label: "All Announcements", href: "/announcements/all" },
      { label: "Financial results", href: "/announcements/financial" },
      { label: "Board Meetings", href: "/announcements/board" },
      { label: "Shareholder Meetings", href: "/announcements/shareholder" },
      { label: "Payouts", href: "/announcements/payouts" },
    ]
  },
  { id: "charts", label: "Bazaar Charts", icon: ChartIcon, href: "/charts" },
  { id: "research", label: "Bazaar Research", icon: CryptoIcon, href: "/research" },
  { id: "news", label: "News", icon: NewsIcon, href: "/news" },
  { id: "chat", label: "Bazaar Baat Cheet", icon: AlertIcon, href: "/baat-cheet" },
  { id: "help", label: "Help & Support", icon: InfoIcon, href: "/help" },
];

export default function ExpandedSideBar() {
  const pathname = usePathname();
  const router = useRouter();
  
  // Auto-expand trading if on trading routes
  const isTradingRoute = pathname.startsWith("/trading/");
  const [expandedItems, setExpandedItems] = useState<string[]>(() => {
    const initial = ["announcements"];
    if (isTradingRoute) {
      initial.push("trading");
    }
    return initial;
  });

  // Update expanded items when pathname changes
  useEffect(() => {
    if (isTradingRoute && !expandedItems.includes("trading")) {
      setExpandedItems(prev => [...prev, "trading"]);
    }
  }, [pathname]);

  const isActive = (href: string) => {
    if (href === "/trading") {
      return pathname.startsWith("/trading/");
    }
    return pathname === href || pathname.startsWith(href + "/");
  };

  const toggleExpand = (id: string) => {
    setExpandedItems(prev => 
      prev.includes(id) ? prev.filter(i => i !== id) : [...prev, id]
    );
  };

  return (
    <div className="w-70 bg-[#F9FAFB] border-r border-[#dadce0] flex flex-col h-screen py-4 overflow-y-auto custom-scrollbar">
      {/* Logo */}
      <div className="px-4 pb-6">
        <ShareBazaarLogo />
      </div>

      {/* Divider */}
      <div className="mx-4 mb-4 h-px bg-[#dadce0]" />

      {/* Menu Items */}
      <div className="flex-1 overflow-y-auto px-2">
        {menuItems.map((item) => (
          <div key={item.id}>
            <Link
              href={item.submenu ? item.submenu[0].href : item.href}
              onClick={(e) => {
                if (item.submenu) {
                  e.preventDefault();
                  toggleExpand(item.id);
                  // Navigate to first submenu item (Platform for trading)
                  router.push(item.submenu[0].href);
                }
              }}
              className={`w-full flex items-center gap-3 px-3 py-2.5 rounded-lg transition-colors mb-1 ${
                isActive(item.href) 
                  ? "bg-white border border-gray-200 shadow-sm" 
                  : "hover:bg-gray-50"
              }`}
            >
              {/* All icons in expanded sidebar use primary color */}
              <item.icon color="primary" />
              <span className={`text-sm font-medium ${isActive(item.href) ? "text-gray-900 font-semibold" : "text-gray-700"}`}>
                {item.label}
              </span>
            </Link>
            
            {/* Submenu - Breadcrumbs style */}
            {item.submenu && expandedItems.includes(item.id) && (
              <div className="ml-8 mb-2 relative">
                {/* Vertical line connecting to parent */}
                <div className="absolute left-0 top-0 bottom-0 w-px bg-gray-200"></div>
                
                <div className="space-y-1 pt-1">
                  {item.submenu.map((sub, index) => (
                    <div key={sub.href} className="relative">
                      {/* Horizontal branch line */}
                      <div className="absolute left-0 top-1/2 w-3 h-px bg-gray-200"></div>
                      
                      <Link
                        href={sub.href}
                        className={`block pl-5 py-1.5 text-sm transition-colors relative ${
                          isActive(sub.href) 
                            ? "text-primary font-medium" 
                            : "text-gray-500 hover:text-gray-700"
                        }`}
                      >
                        {sub.label}
                      </Link>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
